using hiravrt.Models.Nav.Graphs;using Microsoft.AspNetCore.Mvc;using System.Text;using System.Text.Json.Nodes;using System.Text.Json.Serialization;namespace hiravrt.Models.Game {	public abstract class GameModel {		public int MinGuessCount { get; set; } = default;		protected LookUp LookUp { get; }		public int Score { get; set; } = default;		public string CurrentGuess { get; set; } = "";		public List<string> RemainingGuesses { get; set; }		public List<string> CorrectGuesses { get; set; } = [];		public List<string> WrongGuesses { get; set; } = [];		public GameModel(LookUp lookUp, int minGuessCount) {			this.RemainingGuesses = [					"\u3042", "\u3044", "\u3046", "\u3048", "\u304a",					"\u304b", "\u304d", "\u304f", "\u3051", "\u3053",					"\u3055", "\u3057", "\u3059", "\u305b", "\u305d",					"\u305f", "\u3061", "\u3064", "\u3066", "\u3068",					"\u306a", "\u306b", "\u306c", "\u306d", "\u306e",					"\u306f", "\u3072", "\u3075", "\u3078", "\u307b",					"\u307e", "\u307f", "\u3080", "\u3081", "\u3082",					"\u3084",           "\u3086",           "\u3088",					"\u3089", "\u308a", "\u308b", "\u308c", "\u308d",					"\u308f",                               "\u3092",			"\u3093",			];			this.LookUp = lookUp;			this.MinGuessCount = minGuessCount;						FirstGuess();		}		protected abstract void FirstGuess();		protected abstract void NextGuess();		public abstract void NextMove(string syllable);		public abstract bool IsCorrect(string syllable);		public virtual void Reset() {			Score = default;			CurrentGuess = "";			CorrectGuesses.Clear();			WrongGuesses.Clear();			FirstGuess();		}	}}